import { Component, OnInit, Input, EventEmitter, Output } from '@angular/core';
import { AuthenticationService } from '../authentication.service';
import Swal from 'sweetalert2';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  @Input()  email = "";
  @Output() submitted = new EventEmitter<string>();
  nom_complet = "";
  authenticationService : AuthenticationService;
  constructor(authenticationService : AuthenticationService) {
    this.authenticationService = authenticationService;
  }

  ngOnInit(): void {
  }

  register(data: any) {
    console.log("LOL",data, this.authenticationService.register(data));
    var response = "";
    if(data.mdp1 != data.mdp2) {
      Swal.fire(
        'Erreur',
        'Les deux mots de passe entrés ne correspondent pas.',
        'error'
      )
    }
    else if(response) {
      Swal.fire(
        'Terminé !',
        'Votre inscription est terminée.',
        'success'
      )
    }
  }

  async login(data: any) {
    console.log("LOL",data, this.authenticationService.register(data));
    var response = await this.authenticationService.login(data);
    if(!response) {
      Swal.fire(
        'Erreur de connexion',
        'Votre email ou mot de passe est incorrect',
        'error'
      )
    }
    else {
      Swal.fire(
        'Connexion réussie !',
        'Vous etes connecté(e). Vous allez etre redigé(e)',
        'success'
      )
    }
  }

}
